  state = {
    isLoding: true,
    movies: []
  };


getMovies = async () => {
  const {
    data : {
      data: {movies}
    }
  } 
  = await axios.get('https://yts-proxy.now.sh/list_movies.json');
  this.setState({movies, isLoding:false});
};

  componentDidMount() {
